mapper.select:
select count(*) from pay_bill where mch_id = #{mchId}

	AND pay_way = #{payWay}


	AND order_type = #{orderType}


	AND date_format(trading_time, '%Y-%m-%d') = #{billDate}

select

from pay_bill where unit_id = #{unitId}

	AND pay_way = #{payWay}


	AND order_type = #{orderType}


	AND date_format(trading_time, '%Y-%m-%d') = #{billDate}

ORDER BY create_time
limit #{pageNo}, #{pageSize}
