mapper.insert:
insert into pay_config (
	mch_name, mch_id , pay_way, pay_scene, status, config, create_time,  update_time
)
values (
	#{mchName}, #{mchId}, #{payWay}, #{payScene}, #{status}, #{config, javaType=java.util.Map, jdbcType=VARCHAR}, #{createTime}, #{updateTime}
)

mapper.select:
SELECT id, mch_id, mch_name, pay_way, pay_scene, status, config, create_time,  update_time
FROM pay_config
WHERE id = #{id}

SELECT id, mch_id, mch_name, pay_way, pay_scene, status, config, create_time,  update_time
FROM pay_config
WHERE mch_id = #{mchId} AND  pay_way = #{payWay} AND status = #{status} AND pay_scene = #{payScene}

SELECT id, account_name, account_no, pay_way, pay_scene, status, config, create_time,  update_time
FROM pay_config WHERE WHERE 1 = 1

	AND mch_id = #{mchId}

limit #{page}, #{size}

mapper.update:
update pay_config

	
		mch_id=#{mchId},
	
	
		mch_name=#{mchName},
	
	
		pay_way=#{payWay},
	
	
		pay_scene=#{payScene},
	
	
		status=#{status},
	
	
		config=#{config, javaType=java.util.Map, jdbcType=VARCHAR}
	
	
		update_time=#{updateTime}
	

id=#{id}
